float radius  = 0.4f;
float blur = 0.2f;

float DrawCircle(vec2 uv, vec2 center, float radius, float blur) 
{    
    float d = length(center-uv);
    float col = smoothstep(radius, radius-blur, d);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy; 
    //uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec2 mouse = iMouse.xy;
    if (mouse == vec2(0.0))
        mouse = iResolution.xy / 2.0;
    mouse /= iResolution.y;

    vec2 center = mouse.xy;

    float col = 0.0f;
    col += DrawCircle(uv, center, radius, blur);


    vec4 mat = texture(iChannel0, uv);    
    fragColor = mat*col*1.25;
}
