void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec2 p1 = vec2(0.0, 0.0);
    vec2 p2 = iMouse.xy / iResolution.xy;


    float f = 55.0;

    float sync = 0.; // sin(iTime * 1.0); // phase offset
    float beat = 0.; // 20. * sin(iTime); // frequency offset

    float p1_mag = sin(f * length(uv - p1));
    float p2_mag = sin((f + beat) * length(uv - p2) + sync);

    float total = p1_mag + p2_mag;
    // Output to screen
    fragColor = vec4(vec3(total),1.0);
}
