void mainImage(out vec4 color,in vec2 o){
    float ok=OK(floor(o/2.0-floor(iResolution.xy/4.0)),k1,k2);
    ok=floor(ok)*pi/k1/k2+iTime*pi;
    color=vec4(abs(sin(ok)),sin(ok),-sin(ok),0);
}

vec2 mainSound(int samp, float time){
    float ok=OK(vec2(time,time),k1,k2);
    ok=sin(ok)*(CUBE(k1*k2))*2.0*pi*time;
    return vec2(sin(ok));
}

const float pi=3.1416;
const float k1=8.0;
const float k2=4.0;
float CUBE(float n){
    return n*n;
}
float OK(vec2 o,float k1,float k2){
    float x=floor(o.x*k1);
    float y=floor(o.y*k2);
    float z=CUBE(x/k1)+CUBE(y/k2); 
    return z;
}
