vec4 color1 = vec4(0.0, 0.0, 0.0, 1.0);
vec4 color2 = vec4(1.0, 0.0, 0.0, 1.0);
vec4 color3 = vec4(1.0, 1.0, 0.0, 1.0);
vec4 color4 = vec4(0.0, 0.0, 1.0, 1.0);
vec4 color5 = vec4(0.0, 0.0, 0.0, 1.0);

float edge1Pos = 0.05;
float edge2Pos = 0.35;
float edge3Pos = 0.65;
float edge4Pos = 0.95;

float edgeSize = .05;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float time = iTime * 1.2;

    float fromSDFTexel = uv.x + sin((time + uv.y + uv.x * cos(time)) * 1.0) * 0.05;
    float firstColorChange = smoothstep(edge1Pos - edgeSize, edge1Pos + edgeSize, fromSDFTexel);
    float secondColorChange = smoothstep(edge2Pos - edgeSize, edge2Pos + edgeSize, fromSDFTexel);
    float thirdColorChange = smoothstep(edge3Pos - edgeSize, edge3Pos + edgeSize, fromSDFTexel);
    float fourthColorChange = smoothstep(edge4Pos - edgeSize, edge4Pos + edgeSize, fromSDFTexel);

    vec4 pixelColor = mix( mix(color1, color2, firstColorChange), mix(color3, mix(color4, color5, fourthColorChange), thirdColorChange), secondColorChange);


    // Output to screen
    fragColor = pixelColor* texture(iChannel0, uv);    
}
