#define R                           iResolution
#define TO_CENTERED_NDC(SC)       ( ((SC)+(SC)-R.xy) / R.y )         // in range x=[-AR; AR], y=[-1.0; 1.0]
#define NZMX                      ( (iMouse.x+iMouse.x-R.x) / R.x )  // in range [-1.0; 1.0]
#define NZMY                      ( (iMouse.y+iMouse.y-R.y) / R.y )  // in range [-1.0; 1.0]
#define LMB_IS_HOLD               ( iMouse.z > 0.0 )

float Linf(vec2 B, vec2 E)
{
    return max(abs(E.x - B.x), abs(E.y - B.y));
}

#define Y(x) (n*x*x+m*x+k)
float quadraticSDF_Linf(
    vec2 NDC,
    float n,
    float m,
    float k)
{
    float inv2n = 1. / (2.*n);
    float D0 = (m-1.)*(m-1.)-4.*n*(k-NDC.y+NDC.x);
    float D1 = (m+1.)*(m+1.)-4.*n*(k-NDC.y-NDC.x);

    float R = sinOSC(0.0, 0.5, iTime); // thickness;

    float x0 = -m*inv2n;
    vec2 C0 = vec2(x0, Y(x0));
    float minDist = Linf(C0, NDC);

    if (D0 >= 0.0)
    {
        float sqrtD0 = sqrt(D0);
        float x1 = (1.-m+sqrtD0)*inv2n;
        float x2 = (1.-m-sqrtD0)*inv2n;
        vec2 C1 = vec2(x1, Y(x1));
        vec2 C2 = vec2(x2, Y(x2));

        minDist = min(minDist, min(Linf(C1, NDC), Linf(C2, NDC)));
    }

    if (D1 >= 0.0)
    {
        float sqrtD1 = sqrt(D1);
        float x3 = (-m - 1. + sqrtD1) * inv2n;
        float x4 = (-m - 1. - sqrtD1) * inv2n;

        vec2 C3 = vec2(x3, Y(x3));
        vec2 C4 = vec2(x4, Y(x4));

        minDist = min(minDist, min(Linf(C3, NDC), Linf(C4, NDC)));
    }

    return minDist - R;
}

/* IQ coloring scheme */
vec3 colorize_field(float dist, vec3 color)
{
    // Change color if dist is negative.
    color = 1.0 - sign(dist) * color;

    // Attenuate color around edges.
	color *= 1.0 - exp(-4.0 * abs(dist));

    // Add interleaved curves.
	color *= 0.7 + 0.2 * cos(30.0 * dist);

    // Add white edges.
    return mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 6./R.y, abs(dist)));
}

vec3 add_metric_box(
    vec2 NDC,
    float n,
    float m,
    float k,
    vec3 color)
{
    float unit = 7.0 / R.y;

    vec2 M = 
        LMB_IS_HOLD ?
            TO_CENTERED_NDC(iMouse.xy) * 2.0:
            rotateAroundZ(vec2(1, 0), iTime * 0.5) * 1.5;
            //vec2(0);

    // Add point
    color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, length(NDC - M) - 0.008));

    float dist_form_point_to_box_edge = Linf(NDC, M) - abs(quadraticSDF_Linf(M, n, m, k));
    float outline = abs(dist_form_point_to_box_edge);

    // Add metric box
    return mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, outline));
}
/* IQ coloring scheme */

void mainImage( out vec4 O, in vec2 SC )
{
    float n = sinOSC(-4.0, 4.0, iTime * 0.4);
    float m = sinOSC(-4.0, 4.0, iTime * 0.8);
    float k = 0.0;

    vec2 NDC = TO_CENTERED_NDC(SC) * 2.0;
    float minDist = quadraticSDF_Linf(NDC, n, m, k);

    // Blending.
    vec3 color = colorize_field(minDist, vec3(0.1, 0.4, 0.7));
    color = add_metric_box(NDC, n, m, k, color);

    O = vec4(color, 1.0);
}

/* Returns sine modulated value in range [min, max]. */
float sinOSC(in float min, in float max, in float T)
{
    float v = (sin(T) + 1.0) * 0.5; // map T to [0.0, 1.0];
    return min + v * (max - min);   // map T to [min, max];
}

vec2 rotateAroundZ(vec2 p, float a)
{
    float s = sin(a);
    float c = cos(a);

    return vec2(
        p.x * c - p.y * s,
        p.y * c + p.x * s);
}
