#define Pi acos(-1.0)

//  https://www.desmos.com/calculator/icuindny2w
//  Not actually sin(x), it's -cos(x).
float DerpSine(float x) {
    float y = round(abs(abs(mod(x-Pi,2.0*Pi))/Pi-1.0)) * (-1.0*pow((abs(mod(x,2.0*Pi))-Pi)/(Pi/2.0),2.0)+1.0) + round(abs(mod(abs(x),2.0*Pi)/Pi-1.0)) * (pow((abs(mod(x-Pi,2.0*Pi))-Pi)/(Pi/2.0),2.0)-1.0);
    return y;
}


//  https://www.desmos.com/calculator/0fwfcwpzcj
float DerpSmoothStep(float x, float p) {
    float y = round(mod(x-0.5, 1.0)) * pow(2.0*x, p)*0.5 + round(mod(x, 1.0)) * ( -pow(2.0*(-x)+2.0, p)*0.5 + 1.0 );
    return y;
}

float iDerpSmoothStep(float x, float p) {
    float y = round(mod(x-0.5, 1.0)) * pow(2.0*x, 1.0/p)*0.5 + round(mod(x, 1.0)) * ( -pow(2.0*(-x)+2.0, 1.0/p)*0.5 + 1.0 );
    return y;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 ScreenUV = fragCoord/iResolution.xy;

    float DerpTime = DerpSine(iTime) * 0.333 + 0.666;

    float color;
    if      (ScreenUV.y > 0.875) { color =  DerpSmoothStep(ScreenUV.x, 6.0*DerpTime); }
    else if (ScreenUV.y > 0.75 ) { color =  DerpSmoothStep(ScreenUV.x, 3.0*DerpTime); }
    else if (ScreenUV.y > 0.625) { color =  DerpSmoothStep(ScreenUV.x, 2.0*DerpTime); }
    else if (ScreenUV.y > 0.5  ) { color =  DerpSmoothStep(ScreenUV.x, 1.5*DerpTime); }
    else if (ScreenUV.y > 0.375) { color = iDerpSmoothStep(ScreenUV.x, 6.0*DerpTime); }
    else if (ScreenUV.y > 0.25 ) { color = iDerpSmoothStep(ScreenUV.x, 3.0*DerpTime); }
    else if (ScreenUV.y > 0.125) { color = iDerpSmoothStep(ScreenUV.x, 2.0*DerpTime); }
    else                         { color = iDerpSmoothStep(ScreenUV.x, 1.5*DerpTime); }

    fragColor = vec4(vec3(color),1.0);
}
