float SAMPLE = 100.0;

int modd(float a, float b){
    return int (a - (b * floor(a/b)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 texCol = texture(iChannel0, uv);
    vec3 col;

    if((modd(uv.x * SAMPLE, 2.0) == 0 && modd(uv.y * SAMPLE, 2.0) == 0) || (modd(uv.x * SAMPLE, 2.0) != 0 && modd(uv.y * SAMPLE, 2.0) != 0)){
        col = vec3((texCol.x + texCol.y + texCol.z) / 3.0);;
    } else {
        col = vec3(texCol.x, texCol.y, texCol.z);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
