float sinNoise(vec2 p)
{
    return fract(sin(p.x * 100. + p.y * 6574.) * 5647. );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float n_repeat = 20. + 19.*sin(iTime);

    vec2 local_uv = fract(uv * n_repeat);
    vec2 id = floor(uv * n_repeat);

    float c = 1. * sinNoise(uv * n_repeat);
    vec3 col = vec3(c);
    //col.rg = local_uv;
    col.rg = id * 1. / n_repeat;

    fragColor = vec4(col,1.0);
}
