float noise(vec2 uv){
    return fract(sin(uv.x*1600.+uv.y*6574.)*5000.);
}


float smoothNoise(vec2 uv){
    vec2 lv = fract(uv);
    vec2 id = floor(uv);

    lv = lv*lv*(3.-2.*lv);

    float bl = noise(id);
    float br = noise(id+vec2(1.,0));
    //float br = noise(id+vec2(1.0));
    float b = mix(bl,br,lv.x);

    float tl = noise(id+vec2(0,1));
    float tr = noise(id+vec2(1,1));
    float t = mix(tl,tr,lv.x);

    return  mix(b,t,lv.y);
}

vec3 fbm(vec2 uv,float iTime){

    uv+=iTime;

    float c0 = smoothNoise(uv*4.);
    c0 += smoothNoise(uv*8.)*0.5;
    c0 += smoothNoise(uv*16.)*0.25;
    c0 += smoothNoise(uv*32.)*0.125;
    c0 += smoothNoise(uv*64.)*0.0625;


    c0/=2.;
    return vec3(c0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col =fbm(uv,iTime);
    fragColor = vec4(col,1.0);
}
