float roundedBoxSDF(vec2 p, vec2 size, float r) {
    return length(max(abs(p)-size+r,0.0)) - r;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 size = vec2(300.0f, 200.0f);
    vec2 location = iResolution.xy / 2.0f;
    float edgeSoftness  = 2.0f;
    float radius = 30.0f;

    float distance = roundedBoxSDF(fragCoord.xy - location, size / 2.0f, radius);

    float smoothedAlpha = smoothstep(0.0f, edgeSoftness, distance);

    vec3 color = mix(vec3(0), vec3(0.518,0.686,0.996), smoothedAlpha);

    fragColor = vec4(color, 1.0);
}
