vec3 hash32(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}
mat2 Rot(float angle){
    float s=sin(angle), c=cos(angle);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    // Normalized pixel coordinates (from 0 to 1)    
    vec2 uv = fragCoord/iResolution.xy;

    vec2 M = (iMouse.xy-iResolution.xy*.5)/iResolution.y;
    float t=  iTime*.02;
    uv *=Rot(t);
    uv +=M*4.;

    vec2 center = iResolution.xy / 4.0;
    vec2 circle_offset = vec2(center.x * sin(0.22 * iTime), center.y  * cos(0.156 * iTime));

    float a = length(fragCoord + circle_offset - center);
    float offset1 = 70. * sin(0.006 * iTime);
    float offset2 = 50. * cos(0.025 * iTime);
    float offset3 = 65. * cos(0.005 * iTime);
    float offset4 = 45. * sin(0.015 * iTime);

 	float circle0 = sin(0.015 * a);
 	float circle1 = sin(0.0211 * fragCoord.x - offset1);
    float circle2 = cos(0.02 * (fragCoord.y + fragCoord.x) + offset2);
    float circle3 = sin(0.02 * fragCoord.y - offset3);
    float circle4 = cos(0.045 * (fragCoord.y - fragCoord.x) - offset4);
    float circle5 = cos(0.0467 *(fragCoord.y -offset4));

    float value = circle0 -circle1 +circle3 +circle2+ circle5 - circle4;
     vec3 col= vec3(value*0.168,0.,0.478*value)  + 0.15*cos(iTime+uv.xyx+vec3(0.4,0.,0.6));
     col -= (hash32(fragCoord+iTime))*0.3;//noise

    // Output to screen
    fragColor = vec4(col,1.0);
}
