vec3 hue(float val) {
    vec3 col;
    float h = mod(val, 1.0);
    float X = -abs(6.0 * mod(h, 1.0 / 3.0) - 1.0) + 1.0;

    if(h < 1.0 / 6.0)
        col = vec3(1.0,X,0.0);
    else if(h < 2.0 / 6.0)
        col = vec3(X,1.0,0.0);
    else if(h < 3.0 / 6.0)
        col = vec3(0.0,1.0,X);
    else if(h < 4.0 / 6.0)
        col = vec3(0.0,X,1.0);
    else if(h < 5.0 / 6.0)
        col = vec3(X,0.0,1.0);
    else
        col = vec3(1.0,0.0,X);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord;

    // Time varying pixel color
    vec3 col = hue(0.1 * iTime - .002 * length((iResolution.xy / 2.0) - uv));

    // Output to screen
    fragColor = vec4(col,1.0);
}
