void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv /= 2.;

    vec3 col = texture( iChannel0, uv ).rgb;
    fragColor = vec4(col,1.0);
}

vec2 quadBezier( float t, vec2 p1, vec2 p2, vec2 c1 ){
    return pow( 1. - t, 2. ) * p1 + 2. * t * ( 1. - t ) * c1 + t * t * p2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 2.;

    // Time varying pixel color
    vec3 col;

    float resRa = iResolution.x / iResolution.y;

    float p;

    vec2 p1 = vec2( .0, .0 ),
         p2 = vec2( 1., 1. ),
         c1 = vec2( 0., 1. );

    for( float i = 0.; i < 500.; i++ ){
        vec2 path = quadBezier( i / 500., p1, p2, c1 );
        p += 1. - step( 0.004 * ( 1. - i / 550. ), distance( vec2( uv.x * resRa, uv.y ), vec2( path.x * resRa, path.y ) ) );
    }


    col += p;


    // Output to screen
    fragColor = vec4(col,1.0);


}
