#define PI 3.14159265359


mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}


float box(in vec2 st, in vec2 size) {
        size = vec2(0.5) - size*0.5;
        /*vec2 uv = smoothstep(size,
                        size+1.5/iResolution.y,
                        vec2(abs(st.y-st.x)));*/
        vec2 uv = smoothstep(size,
                            size+1.5/iResolution.y,
                            st)
                            * smoothstep(size,
                        size+1.5/iResolution.y,
                        vec2(1.0)-st);

        return uv.x*uv.y;  // intersection
    }

float _cross(in vec2 st, float size){
        return  box(st, vec2(size,size/4.)) +
                box(st, vec2(size/4.,size));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Based on work of Author @patriciogv - 2015, 
    // http://patriciogonzalezvivo.com

    vec2 st = ( fragCoord -.5*iResolution.xy )  /iResolution.y;
    vec3 color = vec3(0.0);

    // rotate the space
    st = rotate2d( sin(iTime)*PI ) * st;
    st += vec2(0.5);

    // Show the coordinates of the space on the background
    //color = vec3(st.x,st.y,0.0);
    color = vec3(0, st);

    // Add the shape on the foreground
    color += vec3(_cross(st,0.5));


    fragColor = vec4(color,1.0);


}
