float Hash21(vec2 p){
    p = fract(p*vec2(234.34, 435.345));
    p+= dot(p, p+34.23);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0); //0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    //uv += iTime*.2;
    uv *= 5.;
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float n = Hash21(id); // random number between 0 and 1

    //col.rg = gv;
    float width = .1;

    if(n<.5) gv.x*=-1.; // random flipping
    float diag = gv.x + gv.y;
    float d = abs(abs(diag)); // Two lines

    vec2 cuv = gv-(sign(diag+.001))*.5;
    float angle = atan(cuv.x, cuv.y); // -pi to pi

    d = length(cuv);
    float mask = smoothstep(.01, -.01, abs(d-.5)-width);
    float checker = mod(id.x + id.y, 2.)*2.-1.;
    float flow = sin(iTime + checker*angle*10.);

    float x = (checker*angle/1.57+iTime*.3);
    float y= (d-(.5-width))/(2.0*width);
    y = abs(y-.5)*2.;
    vec2 tuv = vec2(x, y);
    col += texture(iChannel0, tuv).rgb*mask;
    //col.rg+=n;

    // Border lines
    //if(gv.x>.48 || gv.y >.48) col = vec3(1,0,0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
