const vec3 base_color = vec3(0.2, 0.3, 1.0);
const vec3 blue_shift = vec3(1.0, 1.0, 1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 center = iResolution.xy/2.;

    float c0 = 0., c1 = 0.;

    for(float i = 0.; i < 50.; ++i) {
        float t = 4.*iTime + hash11(i);

        vec2 v = hash21(i + 50.*floor(t));
        t = fract(t);
        v = vec2(sqrt(-2.*log(1.-v.x)), 6.283185*v.y);
        v = 20.*v.x*vec2(cos(v.y), sin(v.y));

        vec2 p = center + t*v - fragCoord;
        c0 += 4.*(1.-t)/(1. + 0.3*dot(p,p));

        p = p.yx;
        v = v.yx;
        p = vec2(
            p.x/v.x,
            p.y - p.x/v.x*v.y
        );

        float a = abs(p.x) < 0.1 ? 50./abs(v.x) : 0.;
        float b0 = max(2. - abs(p.y), 0.);
        float b1 = 0.2/(1.+0.0001*p.y*p.y);
        c0 += (1.-t)*b0*a;
        c1 += (1.-t)*b1*a;
    }

    vec3 rgb = c0*base_color + c1*base_color*blue_shift;
    rgb += hash33(vec3(fragCoord,iTime*256.))/512.;
    rgb = pow(rgb, vec3(0.4545));
    fragColor = vec4(rgb,1.);
}

// https://www.shadertoy.com/view/XlGcRh


// https://www.pcg-random.org/
uint pcg(uint v)
{
	uint state = v * 747796405u + 2891336453u;
	uint word = ((state >> ((state >> 28u) + 4u)) ^ state) * 277803737u;
	return (word >> 22u) ^ word;
}

uvec2 pcg2d(uvec2 v)
{
    v = v * 1664525u + 1013904223u;

    v.x += v.y * 1664525u;
    v.y += v.x * 1664525u;

    v = v ^ (v>>16u);

    v.x += v.y * 1664525u;
    v.y += v.x * 1664525u;

    v = v ^ (v>>16u);

    return v;
}

// http://www.jcgt.org/published/0009/03/02/
uvec3 pcg3d(uvec3 v) {

    v = v * 1664525u + 1013904223u;

    v.x += v.y*v.z;
    v.y += v.z*v.x;
    v.z += v.x*v.y;

    v ^= v >> 16u;

    v.x += v.y*v.z;
    v.y += v.z*v.x;
    v.z += v.x*v.y;

    return v;
}


float hash11(float p) {
    return float(pcg(uint(p)))/4294967296.;
}

vec2 hash21(float p) {
    return vec2(pcg2d(uvec2(p, 0)))/4294967296.;
}

vec3 hash33(vec3 p3) {
    return vec3(pcg3d(uvec3(p3)))/4294967296.;
}
