#define a .001

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord.xy/iResolution.xy;
    vec4 tc = iFrame < 10 ? texture(iChannel0, uv) : mix( texture(iChannel0, uv), texture(iChannel1, uv), .9);

    tc += texture(iChannel1, uv+vec2(-a,-a))*.7;
    tc += texture(iChannel1, uv+vec2(0.,-a))*.0;
    tc += texture(iChannel1, uv+vec2(a, -a))*.3;

    tc += texture(iChannel1, uv+vec2(-a,0.))*.2;
   // tc += texture(iChannel0, uv+vec2(0.,0.))*.5;
    tc += texture(iChannel1, uv+vec2(a, 0.))*.2;

    tc += texture(iChannel1, uv+vec2(-a, a))*.8;
    tc += texture(iChannel1, uv+vec2(0., a))*.0;
    tc += texture(iChannel1, uv+vec2(a,  a))*.5;

    tc *= .27;

    fragColor = tc/(.9+texture(iChannel0, uv)*.4);

}
