void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy-0.5; // normalize and center
    uv.x *= iResolution.x/iResolution.y; // fix aspect ratio

    // background
    vec3 c1 = vec3(1.0, 0.0, 0.0); // red
    vec3 c2 = vec3(1.0, 0.8, 0.0); // yellow
    vec3 blend = mix(c1, c2, length(uv)); // gradient

    // circle
    float u = 0.3; // unit radius
    blend /= smoothstep(u-0.02, u+0.02, length(uv));

    // lines
    blend += cos(iTime-uv.y*200.0+sin(uv.x*10.0+iTime))*0.2;

    // gradient
    blend += sqrt(0.5+uv.y)-0.6;

    // draw
    fragColor = vec4(blend, 1.0);
}
