void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int blurSize = 10;

    if(fragCoord.x < iResolution.x/2.0) {
        blurSize = 1;
    }

    vec2 coord = fragCoord - mod(fragCoord, vec2(float(blurSize), float(blurSize)));
    fragColor = texture(iChannel0, coord/iResolution.xy);
}
