#define PI     3.14159265

struct Arc {
    float innerRadiusSqr;
    float outerRadiusSqr;
    float startingAngle;
    float endingAngle;
};

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 origin = iResolution.xy / 2.0;
    vec2 dst = vec2(fragCoord.x - origin.x, fragCoord.y - origin.y);
    float dstSqr = pow(dst.x, 2.0) + pow(dst.y, 2.0);

    Arc arcs[10] = Arc[10](
      Arc(pow(90.0, 2.0), pow(100.0, 2.0), 0.0, mod(iTime * 100.0, 360.0)),
      Arc(pow(100.0, 2.0), pow(110.0, 2.0), 0.0, mod(iTime * 95.0, 360.0)),
      Arc(pow(110.0, 2.0), pow(120.0, 2.0), 0.0, mod(iTime * 90.0, 360.0)),
      Arc(pow(120.0, 2.0), pow(130.0, 2.0), 0.0, mod(iTime * 85.0, 360.0)),
      Arc(pow(130.0, 2.0), pow(140.0, 2.0), 0.0, mod(iTime * 80.0, 360.0)),
      Arc(pow(140.0, 2.0), pow(150.0, 2.0), 0.0, mod(iTime * 75.0, 360.0)),
      Arc(pow(150.0, 2.0), pow(160.0, 2.0), 0.0, mod(iTime * 70.0, 360.0)),
      Arc(pow(160.0, 2.0), pow(170.0, 2.0), 0.0, mod(iTime * 65.0, 360.0)),
      Arc(pow(170.0, 2.0), pow(180.0, 2.0), 0.0, mod(iTime * 60.0, 360.0)),
      Arc(pow(180.0, 2.0), pow(190.0, 2.0), 0.0, mod(iTime * 55.0, 360.0))
    );

    for (int i = 0; i < 10; i++) {
      Arc arc = arcs[i];

      if (dstSqr > arc.innerRadiusSqr && dstSqr < arc.outerRadiusSqr) {
        float radian = atan(dst.x, dst.y);

        if (radian < 0.0) {
          radian = PI + PI + radian;
        }

        float angle = radian * (180.0 / PI);

        if (angle >= arc.startingAngle && angle <= arc.endingAngle) {
          fragColor = vec4(col,1.0);    
        } else {
          fragColor = vec4(0.0);
        }
      }
    }
}
