float n(vec2 uv){return fract(sin(uv.x*124.2+uv.y*2374.73)*8667.673);}

vec4 SmootTextureNoiseMipLv(sampler2D textures, vec2 uv, float size){

    vec2 d = vec2(n(uv), n(vec2(uv.y,uv.x)));

    vec4 defcolor = texture(textures, uv, size/1.2);

    float f = (1.-(1./(size+1.)));

    f *= f;

    uv += (d-0.5)*f/9.;

    return mix(defcolor, texture(iChannel0, uv, size), f );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float mx = iMouse.x/iResolution.x;
    uv.x += iTime/4.;

    fragColor = SmootTextureNoiseMipLv(iChannel0, uv, mx*8.);
