void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time=iTime*.5;
    vec2 uv = -1.0 + 2.0 * fragCoord/iResolution.xy;

    uv.x *= iResolution.x/iResolution.y;

	float c=length(uv);

    float r,g,b;

    float d=3.+sin(time*.5)*3.5;

    c=smoothstep(.125,.95,c);

    r=sin(time+(c+sin(1.+time+uv.y*.5))*d);

    g=sin(time+(c+sin(1.75+time+uv.y*.5))*d);

    b=sin(time+(c+sin(2.25+time+uv.y*.5))*d);



    fragColor = vec4(vec3(r,g,b)+(c-.5+sin(time*120.)*.04),1.0);
}
