float get_depth_map (vec2 uv) {
    uv.x = uv.x * 2. - .5;
    return max(max(abs(uv.y * 2. - 1.),
                   smoothstep(.4,.0,length(uv - vec2(.2, .5)))),
                   smoothstep(.2,.0,length(uv - vec2(.85, .5))));
}

float get_depth_map_anim (vec2 uv) {
    vec2 p = uv - 0.5;
    p.x *= iResolution.x / iResolution.y;    
    return 0.1 * sin((length(p) + iTime * 0.1) * 20.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 16.0;
    vec2 res = iResolution.xy;
    vec2 vUv = fragCoord / res;
    vec2 uv = vUv;
    for(float i = .0; i < res.x; i ++) {
        float inc = float(i)/res.x;
        if (inc < vUv.x) {
            uv.x += (get_depth_map(vec2(inc, vUv.y))) / res.x;
        }
    }

    uv = fract(uv * scale);

    if (iMouse.z > 0.0) fragColor = vec4(vec3(get_depth_map(vUv)), 1);
    else fragColor = texture(iChannel0, uv);
}
