#define r 0.4;
#define N 500.
#define Eps 0.00001
#define rep 2.0

vec3 mod1(vec3 p)
{

    p.x = mod(p.x,rep);

    p.y = mod(p.y,rep);

    p.z = mod(p.z,rep);

    return p;
}

float sphere(vec3 p)
{
    return length(mod1(p)-rep/2.0)-r;
}

bool inside(float value, float step){
    return ((step <= value) && (value <= 2.0*step));
}

float distanceInside(float p1, float p2, float p3){
    p1 = min(p1,abs(1.0-p1));
    p2 = min(p2,abs(1.0-p2));
    p3 = min(p3,abs(1.0-p3));

    return min(min(sqrt(p1*p1+p2*p2),sqrt(p1*p1+p3*p3)),sqrt(p2*p2+p3*p3));
}

float cube(vec3 p){

   float movx = 0.0;
   float movy = 0.0;
   float movz = 0.0;
   float dix = 0.0;
   float diy = 0.0;
   float diz = 0.0;
    //cub en 0 -- 3

    //Si estem fora del cub
    if (!(0.0 <= p.x) || !(p.x <= 3.0)) {
        movx = min(abs(p.x-3.0),abs(p.x));
    }else if ((inside(p.x,1.0) && inside(p.y,1.0)) || (inside(p.x,1.0) && inside(p.z,1.0))){
        float x = mod(p.x,1.0);
        dix = min(x,1.0-x);
    }
    if (!(0.0 <= p.y) || !(p.y <= 3.0)) {
        movy = min(abs(p.y-3.0),abs(p.y));
    }else if ((inside(p.y,1.0) && inside(p.x,1.0)) || (inside(p.y,1.0) && inside(p.z,1.0))){
        float y = mod(p.y,1.0);
        diy = min(y,1.0-y);
    }
    if (!(0.0 <= p.z) || !(p.z <= 3.0)) {

        movz = min(abs(p.z-3.0),abs(p.z));
    }else if ((inside(p.z,1.0) && inside(p.x,1.0)) || (inside(p.z,1.0) && inside(p.y,1.0))){
        float z = mod(p.z,1.0);
        diz = min(z,1.0-z);
    }


    if ((dix>0.0) && (diy>0.0) && (diz>0.0)){
        return distanceInside(mod(dix,1.0),mod(diy,1.0),mod(diz,1.0));
    }
    if ((dix>0.0) && (diy>0.0)){
        return sqrt(min(dix,diy)*min(dix,diy) + movz*movz);
    }
    if ((dix>0.0) && (diz>0.0)){
        return sqrt(min(dix,diz)*min(dix,diz) + movy*movy);
    }
    if ((diz>0.0) && (diy>0.0)){
        return sqrt(min(diz,diy)*min(diz,diy) + movx*movx);
    }
    return sqrt(movx*movx+movy*movy+movz*movz); 
}



vec3 march(vec3 p, vec3 dir)
{
    float dist = 0.0;
    for(int i=0; i<int(N); i++){
        dist = cube(p)-Eps;
        if(dist<Eps){
            return vec3(1.0,1.0,1.0)/log(float(i+1)+1.0);
        }
        p = p + dist*dir;
    }
    return vec3(0.0,0.0,0.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 camPos = textureLod( iChannel0, vec2(.5,.5)/iResolution.xy, 0.0 );
    vec4 camRot = textureLod( iChannel0, vec2(1.5,.5)/iResolution.xy, 0.0 )*6.28318530718;

    // cast a ray from the camera

    vec3 ray = normalize( vec3( (fragCoord.xy-iResolution.xy*.5)/iResolution.x, 1.0 ) );

    ray.zy = ray.zy*cos(camRot.x) + sin(camRot.x)*vec2(1,-1)*ray.yz;
    ray.xz = ray.xz*cos(camRot.y) + sin(camRot.y)*vec2(1,-1)*ray.zx;

    vec3 p = camPos.xyz;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    uv.x -= 0.5;
    // CÃ mera position
    vec3 obs = camPos.xyz;//ec3(0.0,0.0,-5.0);

    vec3 rayL = vec3(uv-vec2(0.5,0.5),-2.0);

    // Direction
    vec3 dir = normalize(rayL-obs);

    vec3 col = march(p,ray);

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Hazel Quantock 2016
