#define INVERT_Y 1

const float accel = .01;
const float decay = .95; // how much velocity is preserved per frame (proportionally)

#if INVERT_Y
const float yMul = 1.0;
#else
const float yMul = -1.0;
#endif


// keys are javascript keycode
const int Key_A = 65; const int Key_B = 66; const int Key_C = 67; const int Key_D = 68; const int Key_E = 69;
const int Key_F = 70; const int Key_G = 71; const int Key_H = 72; const int Key_I = 73; const int Key_J = 74;
const int Key_K = 75; const int Key_L = 76; const int Key_M = 77; const int Key_N = 78; const int Key_O = 79;
const int Key_P = 80; const int Key_Q = 81; const int Key_R = 82; const int Key_S = 83; const int Key_T = 84;
const int Key_U = 85; const int Key_V = 86; const int Key_W = 87; const int Key_X = 88; const int Key_Y = 89;
const int Key_Z = 90;
const int Key_0 = 48; const int Key_1 = 49; const int Key_2 = 50; const int Key_3 = 51; const int Key_4 = 52;
const int Key_5 = 53; const int Key_6 = 54; const int Key_7 = 55; const int Key_8 = 56; const int Key_9 = 57;
const int Key_Space = 32;

float ReadKeyInternal( int key, bool toggle )
{
	return textureLod( iChannel3, vec2( (float(key)+.5)/256.0, toggle?.75:.25 ), 0.0 ).x;
}

float ReadKey( int key )
{
	return ReadKeyInternal(key,false);
}

float ReadKeyToggle( int key )
{
	return ReadKeyInternal(key,true);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0,0.0,0.0,1.0);

    if ( int(fragCoord.y) == 0 )
    {
        if ( int(fragCoord.x) == 0 )
        {
            vec4 camPos = textureLod( iChannel0, vec2(.5,.5)/iResolution.xy, 0.0 );
            vec4 camVel = textureLod( iChannel0, vec2(3.5,.5)/iResolution.xy, 0.0 );

            fragColor = camPos + camVel;

            // fly
            fragColor.w = ReadKeyToggle(Key_F);
        }
        else if ( int(fragCoord.x) <= 2 )
        {
            vec4 baseCamRot = textureLod( iChannel0, vec2(2.5,.5)/iResolution.xy, 0.0 );
            vec4 camRot = textureLod( iChannel0, vec2(1.5,.5)/iResolution.xy, 0.0 );

            vec2 mouseRot = (iMouse.yx/iResolution.yx-.5)*vec2(.5*yMul,1.);

            camRot.w = iMouse.z;

            bool press = (camRot.w > .0);
            bool lastPress = (baseCamRot.w > .0);
            bool click = press && !lastPress;
            if ( click )
            {
                baseCamRot.xy -= mouseRot;
            }

            if ( press )
            {
                camRot.xy = baseCamRot.xy + mouseRot;
            }
            else
            {
                //update the base pos
                baseCamRot = camRot;
            }

            baseCamRot.w = camRot.w;

            // store
            if ( int(fragCoord.x) == 1 )
            {
            	fragColor = camRot;
            }
            else
            {
            	fragColor = baseCamRot;
            }
        }
        else if ( int(fragCoord.x) == 3 )
        {
            vec4 camPos = texture( iChannel0, vec2(.5,.5)/iResolution.xy, -100.0 );
            vec4 camVel = texture( iChannel0, vec2(3.5,.5)/iResolution.xy, -100.0 );
            vec4 camRot = texture( iChannel0, vec2(1.5,.5)/iResolution.xy, -100.0 )*6.28318530718;

            bool fly = (camPos.w > .0);

            vec3 forward = vec3(0,0,1)*accel;
            vec3 up = vec3(0,1,0)*accel;
            vec3 right = vec3(1,0,0)*accel;

            if ( fly ) // allow flying
            {
                forward.zy = forward.zy*cos(camRot.x) + sin(camRot.x)*vec2(1,-1)*forward.yz;
                right.zy = right.zy*cos(camRot.x) + sin(camRot.x)*vec2(1,-1)*right.yz;
            }

            forward.xz = forward.xz*cos(camRot.y) + sin(camRot.y)*vec2(1,-1)*forward.zx;
		    right.xz = right.xz*cos(camRot.y) + sin(camRot.y)*vec2(1,-1)*right.zx;

            camVel.xyz += (ReadKey(Key_W)-ReadKey(Key_S)) * forward;
            camVel.xyz += (ReadKey(Key_D)-ReadKey(Key_A)) * right;
            camVel.xyz += (ReadKey(Key_U)-ReadKey(Key_J)) * up;
            camVel *= decay; // exponential decay

            if ( !fly )
            {
            	camVel.y -= .01;

            	if ( camPos.y <= .0 )
            	{
                	camVel.y = (ReadKey(Key_Space) != .0) ? .3 : 0.0;
            	}
            }

            fragColor = camVel;
        }
    }
}
