#define MAX 3.0
#define CH 0.5
#define SPEED 0.8
#define X 0.5
#define Y 0.5

float invlerp(float a, float b, float t) {
    return (t - a) / (b - a);
}

float lerp(float a, float b, float t) {
    return (1.0 - t) * a + b * t;
}

float A() {
    return lerp(2.0, MAX, ((cos(iTime*SPEED)+1.0) / 2.0));
}

float B() {
    return A()/2.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float cur = 1.0;
    float l,r,b,t;
    float light = 1.0;
    bool flip = false;
    for (int i=0;i<8;i++) {
        l = X - cur/A();
        r = X + cur/A();
        t = Y + cur/A();
        b = Y - cur/A();
        if(uv.x>l && uv.x<r && uv.y>b && uv.y<t) {
            cur /= B();
            if(uv.x*uv.y>=0.25) light*=1.0+(flip ? CH : -CH);
            else light*=1.0+(flip ? -CH : CH);
            flip = !flip;
        } else {
            break;
        }
    }
    vec2 from = vec2(invlerp(X - cur/2.0, X + cur/2.0, uv.x), invlerp(Y - cur/2.0, Y + cur/2.0, uv.y));
    vec3 color = texture(iChannel0, from).bgr;
    fragColor = vec4(color, 1);
}
