vec3 pattern( in vec2 uv )
{   vec3 col = vec3(0.4);
    col += 0.4*smoothstep(-0.01,0.02,cos(uv.x*0.5)*cos(uv.y*0.5)); 
    col *= smoothstep(-1.0,-0.98,cos(uv.x))*smoothstep(-1.0,-0.98,cos(uv.y));
    return col;
}

vec3 FivePointStar(vec2 p, vec2 center, float rmin, float rmax, vec3 starColor)
{
    float npoints = 5.0;
    float starAngle = 2.0*3.1415926/npoints;//72åº¦
    vec3 p0 = rmax*vec3(cos(starAngle/2.0),sin(starAngle/2.0),0.0);
    vec3 p1 = rmin*vec3(cos(0.0),sin(0.0),0.0);
    vec3 d = p1-p0;
    p -= center;
    float r = sqrt(p.x*p.x+p.y*p.y);
    float angle = atan(p.y,p.x)+3.1415926-0.28;
    float a = mod(angle,starAngle)/starAngle;
    if(a>=0.5) 
        a = 1.0-a;
    vec3 d1 = r*vec3(cos(a*starAngle),sin(a*starAngle),0) - p0;

    float in_out = smoothstep(0.0,0.01,cross(d,d1).z);

    vec3 col = mix(starColor, vec3(1.0,0.0,0.0),in_out);
    return col;

}

vec3 Estrella2d( in vec2 fragCoord )
{
   vec2 p=fragCoord.xy;
    p.x= opRep1D(  p.x, 2.0 );
    p.y= opRep1D(  p.y, 2.0 );
    vec3 col = FivePointStar(p,vec2(0.0,0.0),0.25,0.7,vec3(1.0,0.9,0.0));
    return col;
}

//------------------------------------------------
vec3 getMaterial( vec3 pp, float id_material)
{ vec3 col=vec3(1.0);
  vec3 p=pp;
  vec3 l1;


    if (id_material==7.0)
    {return pattern( p.xz );}

    if (id_material==8.0)
    {return pattern( p.xy );}

    if (id_material==9.0)
    {return pattern( p.zy );}


    if (id_material==10.0)
    {return  Estrella2d(p.xz*0.5 );}

}

//-------------------------------------------------
vec3 GetColorYMaterial(vec3 p,  vec3 n, vec3 ro,  vec3 rd, int id_color, float id_material)
{  	vec3 colobj; 

    if( mObj.hitbln==false) return  render_sky_color(rd);

    if (id_color<100)
		{ colobj=getColor(int( id_color)); }



    if (id_material>-1.0 && id_color==-1)
        { 
            colobj=vec3(0.5);
            colobj*=getMaterial(p, id_material); 
            return colobj;
        }
    return colobj;
}

vec3 renderReflect(TObj Obj, vec3 ro, vec3 rd, vec3 col )
{  vec3 p;
   float t;
   vec3 colobj;  vec3 n;

  /////-----------REFLECT--------------
    if (int( Obj.id_color) ==201)
    { vec3 colref;
      for(int i=0; i<2; ++i)
      {  t=RayMarch(ro,rd, MAX_STEPS);
         Obj=mObj;
         if(t>MAX_DIST)
           {
             colobj= render_sky_color(rd);
             return  colobj;
           }  

        if( t<0.0 || t>MAX_DIST ) break;
        p=ro+rd*t;
        n=GetNormal(p);
        rd=reflect(rd,n);
        ro =p+rd * MIN_DIST * 3.;

        if (t<MAX_DIST)
        colobj=GetColorYMaterial( p, n, ro, rd,  int( Obj.id_color), Obj.id_material)*2.0;

        colref=  Getluz( p,ro,rd, n, colobj ,light_pos1);
        colref+= Getluz( p,ro,rd, n, colobj ,light_pos2);
        //colref/=1.25;

       }
     col+=colref/(float(REFLECT));
   } 
   return col;
} 

vec3 Render(vec3 ro, vec3 rd)
{  vec3 col = vec3(0);
   TObj Obj;
   mObj.rd=rd;
   mObj.ro=ro;
   vec3 p;

   float d=RayMarch(ro,rd, MAX_STEPS);

    Obj=mObj;
    if(mObj.hitbln) 
    {   p = (ro + rd * d );  
        vec3 nor=mObj.normal;
        vec3 colobj;
        colobj=GetColorYMaterial( p, nor, ro, rd,  int( Obj.id_color), Obj.id_material);

        float dif1=1.0;
        vec3 result;
        result=  Getluz( p,ro,rd, nor, colobj ,light_pos1);
        result+= Getluz( p,ro,rd, nor, colobj ,light_pos2);
        col= result;
    }
    else if(d>MAX_DIST)
    col= render_sky_color(rd);

    col= renderReflect(Obj,  ro,  rd,  col );
   return col;
}

///---------------------------------------------
vec3 linear2srgb(vec3 c) {
    return mix(
        12.92 * c,1.055 * pow(c, vec3(1.0/1.8)) - 0.055,
        step(vec3(0.0031308), c));
}

vec3 exposureToneMapping(float exposure, vec3 hdrColor) 
{    return vec3(1.0) - exp(-hdrColor * exposure);  }



// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/index.htm
vec3 postProcess(vec3 col, vec2 q) 
{
  col = clamp(col, 0.0, 1.0);
  col = pow(col, 1.0/vec3(2.2));
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col *=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}

///https://www.shadertoy.com/view/llKyDc
float hash21(vec2 p)
{
	return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}


float white_noise(vec2 p){
    return hash21(ceil(fract(0.77)*12.0)*p);
}



///---------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv=(fragCoord-.5*iResolution.xy)/iResolution.y;;
    float tt=mod(iTime,5.0);
    float f=0.0;

    if (tt>0.0 && tt<1.0)
    {
     uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    } 
   else if(tt>1.0 && tt<2.0)
   {
     uv = (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 st = fragCoord / iResolution.xy;
    float tt=iTime*2.0;
	float w = sin((uv.x + uv.y - tt * .75 + sin(1.5 * uv.x + 4.5 * uv.y) * PI * .3)
                  * PI * .6); // fake waviness factor
    uv *= 1. + (.036 - .036 * w);
   } 

   else if(tt>2.0 && tt<3.0  )
   {
        f =white_noise(uv+vec2(0.,0.));
        f+=white_noise(uv+vec2(0.,1.));
        f+=white_noise(uv+vec2(1.,0.));
        f+=white_noise(uv+vec2(1.,1.));
    }




   mObj.uv=uv;
    float t;
    t=mod(iTime*1.0,360.0);
    itime=t;
	//mObj.blnShadow=false;
    mObj.blnShadow=true;

 	light_pos1= vec3(5.0, 10.0, -10.0 ); light_color1=vec3( 1.0 );
 	light_pos2= vec3( -10.0, 30.0, -10.0 ); light_color2 =vec3( 1.0 ); 


   vec3 ro=vec3(0.0,7.0,-25.0);

   vec3 rd;
   rd=normalize( vec3(uv.x,uv.y,1.0));

   if(tt>3.0 && tt<4.0)
    rd= rotate_x(rd,  radians(20.0));


    vec3 col= Render( ro,  rd);
    col = linear2srgb(col);

    vec3 col2=postProcess(col,vec2(0.25));
    col*=(col2*1.3);

    if (tt>2.0 && tt<4.0)
    {
        f = 0.5 + 0.5*f;

        if(uv.y<0.4 && uv.y>0.2)
        {
        f *= smoothstep(0.0, 0.005, abs(uv.y+0.4));
        f *= smoothstep(0.0, 0.005, abs(uv.y+0.2));

        f *= smoothstep(0.0, 0.005, abs(uv.y+0.2));
        f *= smoothstep(0.0, 0.005, abs(uv.y+0.0));
        }    
        else if(uv.y>-0.4 && uv.y<-0.2)
        {
        f *= smoothstep(0.0, 0.005, abs(uv.y+0.0));
        f *= smoothstep(0.0, 0.005, abs(uv.y-0.2));


        f *= smoothstep(0.0, 0.005, abs(uv.y-0.2));
        f *= smoothstep(0.0, 0.005, abs(uv.y-0.4));


        }else
        col=mix(col,vec3(f,f,f),0.35);
    }    



    if (tt>4.0 && tt<5.0 )
    {
        float r = pow(1.0-sqrt( abs(uv.y-0.2)),1.5 );
        float g = pow(1.0-sqrt( abs(uv.y-0.4)),1.5 );
        float b = pow(1.0-sqrt( abs(uv.y-0.6)),1.5 );
        float c = smoothstep(.0, .2, cos( 4. + 8.*PI*length(uv) ) );
        col=mix(col,vec3(c*r,c*g,c*b),0.35);
    }
    fragColor = vec4(col,1.0);
}
