const int kSize = 20;
const float sigma = float(kSize) * .25;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    vec2 stepWidth = 1. / iChannelResolution[0].xy;

    float w = 0.;
    vec3 wc = vec3(0.);

    for (int y = -kSize; y <= kSize; y++) {
         for (int x = -kSize; x < kSize; x++) {
             float g = float(x * x + y * y) / (2. * (sigma * sigma));
             g = exp(-g);

             wc += texture(iChannel0, uv + vec2(x, y) * stepWidth).rgb * g;
             w += g;

         }
    }

    vec3 col = wc / w;

    fragColor = vec4(col, 1.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    fragColor = texture(iChannel0, uv);
}
