#define bridthness 1.2

vec3 point(float dist, float phase, float speed){
    float r = abs(cos(radians(iTime*speed + phase)))*1.5/dist ;
    float g = abs(cos(radians(iTime*speed+90. + phase)))*1.25/dist;
    float b = abs(cos(radians(iTime*speed+45. + phase)))*2./dist;
    return vec3(r, g, b);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy/2.;

    vec3 col = vec3(0.,0.,0.);
    for(float i = 0.; i < 21.; i++){
        col += point(
            distance(fragCoord.xy, iResolution.xy/2. + i*10.),
            i*18.f,
            48.f
        );
    }

    for(float i = 0.; i < 21.; i++){
        col += point(
            distance(fragCoord.xy, iResolution.xy/2. + vec2(i*10., 0.)),
            i*18.f,
            -48.f
        );
    }

     for(float i = 0.; i < 21.; i++){
        col += point(
            distance(fragCoord.xy, iResolution.xy/2. + vec2(200., i*10.)),
            i*18.f,
            -48.f
        );
    }

    fragColor = vec4(col/bridthness, 1.);
}
