    bool lineOver(vec2 uv, vec3 line) 
    {
      return uv.y * line.y >= uv.x * line.x + line.z;
    }


    bool lineUnder(vec2 uv, vec3 line) 
    {
      return uv.y * line.y <= uv.x * line.x + line.z;
    }


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.0*fragCoord/iResolution.xy - 1.0;
          uv.x *= iResolution.x/iResolution.y; 
    vec2 mv = 2.0*iMouse.xy/iResolution.xy - 1.0;

    vec4 red     = vec4(1.0, 0.2, 0.2, 1.0);
    vec4 darkred = vec4(0.7, 0.1, 0.1, 1.0);
    vec4 white   = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 black   = vec4(0.0, 0.0, 0.0, 1.0);
    vec4 body    = vec4(1.0, 0.75, 0.5, 1.0);
    vec4 pink    = vec4(1.0, 0.4, 0.4, 0.5);
    vec4 col     = vec4(0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)), 1.0);

    fragColor = white;

    //head
     if(circle(uv, vec2(0.5, 0.1), 0.35))
       fragColor = body;

     if(circle(uv, vec2(0.4, -0.005), 0.08))
       fragColor = black;

     if(circle(uv, vec2(0.41, -0.04), 0.085))
       fragColor = body;

     if(circle(uv, vec2(0.45, 0.22), 0.11))
       fragColor = pink;

     if(lineOver(uv, vec3(1.0, -16.0, 0.5 + 0.5 * mv.x)) && lineUnder(uv, vec3(-1.0, -16.0, 2.2 + 0.5 * mv.y)) && uv.x >= -0.17) //nose
       fragColor = body;

     if(circle(uv, vec2(0.265, 0.25), 0.070))
       fragColor = black;

     if(circle(uv, vec2(0.26, 0.21), 0.083))
       fragColor = body;

    //hat

     //hat base 
     if(uv.y >= 0.12 && uv.y <= 0.24 && uv.x >= -0.77 && uv.x <= -0.23)
       fragColor = darkred;

     if(circle(uv, vec2(0.23, -0.18), 0.06))
       fragColor = darkred;

     if(circle(uv, vec2(0.77, -0.18), 0.06))
       fragColor = darkred;

     if(uv.y >= 0.14 && uv.y <= 0.23 && uv.x >= -0.77 && uv.x <= -0.23)
       fragColor = red;

     if(circle(uv, vec2(0.23, -0.185), 0.045)) 
       fragColor =  red;

     if(circle(uv, vec2(0.77, -0.185), 0.045)) 
       fragColor =  red;

     //cone
     if(lineOver(uv, vec3(-3.0, -1.0, -2.5)) && lineUnder(uv, vec3(-3.0, 1.0, -0.5)) && uv.y >= 0.24 && uv.y <= 0.9) 
       fragColor = red;

     if(lineUnder(uv, vec3(-3.0, -1.0, -2.3)) && lineOver(uv, vec3(-3.0, -1.0, -2.5)) && uv.y >= 0.24 && uv.y <= 0.9) //cone shadow
       fragColor = darkred;

     if(circle(uv, vec2(0.5, -0.85), 0.10)) // tip
       fragColor = col;

     if(circle(uv, vec2(0.47, -0.89), 0.05)) // tip
       fragColor = col * 1.2;

}
