void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

vec4 circle(float radius, vec2 position, vec2 uv, vec3 color){
    float glow = 0.09;
    float res = smoothstep(radius, radius/5.-glow, length(uv-position));
    return vec4(vec3(res)/color,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord -.5* iResolution.xy) / iResolution.y;
    vec4 config = texture(iChannel1, fragCoord.xy / iResolution.xy);
    vec4 circles = circle(0.1, vec2(0.0, 0.0), uv, vec3(0.4, 0.4, 0.1))
        +circle(0.02, vec2(cos(iTime)*0.2, sin(iTime)*0.2), uv, vec3(0.5, 0.7, .8))
        +circle(0.025, vec2(cos(iTime)*-0.3, sin(iTime)*-0.3), uv, vec3(0.2, 0.5, .5))
        +circle(0.04, vec2(sin(iTime)*-0.5, cos(iTime)*0.5), uv, vec3(0.8, 0.5, .7))
        +circle(0.03, vec2(cos(iTime)*-0.4, sin(iTime)*0.4), uv, vec3(0.8, 0.5, .7))
        +circle(0.05, vec2(sin(iTime)*0.8, cos(iTime)*-0.8), uv, vec3(0.7, 0.3, .3));

    vec4 pfragColor = texture(iChannel0, fragCoord.xy / iResolution.xy)/1.05;

    fragColor = circles+pfragColor;
}
