float circle (vec2 uv, vec2 pos, float r, float blur){
    float dist = length(uv-pos);
    //float center = smoothstep(r,r - blur, dist);
    float center = float(dist < r);
    //float center = 1. - dist;
    return center;
}

vec2 getCentralizedUV(vec2 fragCoord){
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;
    return uv;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = getCentralizedUV(fragCoord);


    float c = circle(uv, vec2(.4,.0), .4, .02)
            + circle(uv, vec2(-.4,.0), .2, .02)
            - circle(uv, vec2(.4,.0), .2, .02);    

    fragColor = vec4(vec3(c),1.0);


}
