#define TEX1(v) texture(iChannel0,vec2(t.x+float(v),t.y+float(v))).rgb;
#define TEX2(v) texture(iChannel1,vec2(t.x+float(v),t.y+float(v))).rgb;
void mainImage( out vec4 f,  vec2 t )
{
    // Normalized pixel coordinates (from 0 to 1)
    t = t/iResolution.xy+iTime;

    // Output to screen
    vec3 co;

for(float i = 0.; i<20.; i+=2.){

    co += TEX1(float(i)); 
    }
co *= TEX2(2.);
f = vec4(co,1.0);

}
