void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = ( u+u - R ) / R.y;
    float w = 3./R.y;
    O -= O;
    for( int i = 0; i < 7; i++  )
        U = length(U) * cos( (mod( 6.*atan(U.y,U.x)+3.14, 6.28)-3.14)/6. +vec2(0,11) ),
        O += smoothstep( w, -w, U.x+1.73*abs(U.y) - 1. ),
        U *= 3., U.x -= 2., w *= 3.;
}
