#define R iResolution
#define s(v,l) smoothstep(l/R.y,0.,v)
#define m(x) (ar=min(ar,(x)))

float lnsg(vec2 p, vec2 a, vec2 b)
{
    vec2 ba = b-a, pa = p-a;
    return length(pa-clamp(dot(pa,ba)/dot(ba,ba),0.,1.)*ba);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = (fragCoord - R.xy * .5) / R.y;
    if (u.x < 0.) u.y = -u.y;
    u.x = abs(u.x);
    const float a = .05, h = a * .866, C = .577;
    const vec3 c1 = vec3(237./255.,226./255.,222./255.), 
               c2 = vec3(238./255.,213./255.,183./255.),
               c3 = vec3(223./255.,197./255.,170./255.),
               c4 = vec3(231./255.,192./255.,175./255.),
               c5 = vec3(219./255.,182./255.,174./255.),
               c6 = vec3(168./255.,140./255.,117./255.);
    vec3 col = vec3(.95);
    float ar;
    u.y -= 2.*a;
    vec2 p1,p2,p3,p4,p5,p6,p7;
    for (float i = 0.; i < 4.; i++)
    {
        ar = 999.;
        float X = u.x, Y = u.y;
        p1=vec2(0.,a/2.);p2=vec2(0.,1.5*a);p3=vec2(h,a);p4=vec2(h,0.);
        p5=vec2(2.*h,2.5*a);p6=vec2(3.*h,2.*a);p7=vec2(3.*h,a);
        m(lnsg(u,p1,p2));m(lnsg(u,p2,p3));m(lnsg(u,p3,p4));m(lnsg(u,p4,p1));
        ar*=sign(3.5-sign(X)-sign(-X+h)-sign(Y+C*(X-h))-sign(-Y-C*(X-3.*h)));
        col = mix(col,mix(c1,c2,clamp(abs(X)/h,0.,1.)),s(ar, 1.1));
        ar = 999.;
        m(lnsg(u,p2,p5));m(lnsg(u,p5,p6));m(lnsg(u,p6,p3));m(lnsg(u,p3,p2));
        ar*=sign(3.5-sign(Y+C*(X-3.*h))-sign(-Y-C*(X-7.*h))-sign(Y-C*(X+h))-sign(-Y+C*(X+3.*h)));
        col = mix(col,mix(c3,c4,clamp(abs(X)/(3.*h),0.,1.)),s(ar, 1.1));
        ar = 999.;
        m(lnsg(u,p4,p3));m(lnsg(u,p3,p6));m(lnsg(u,p6,p7));m(lnsg(u,p7,p4));
        ar*=sign(3.5-sign(X-h)-sign(-X+3.*h)-sign(Y-C*(X-h))-sign(-Y+C*(X+h)));
        col = mix(col,mix(c5,c6,clamp(abs(X-h)/(2.*h),0.,1.)),s(ar, 1.1));
        p1=vec2(3.*h,a);p2=vec2(5.*h,0.);p3=vec2(6.*h,a/2.);p4=vec2(6.*h,1.5*a);
        p5=vec2(5.*h,a);p6=vec2(3.*h,2.*a);p7=vec2(4.*h,2.5*a);
        ar = 999.;
        m(lnsg(u,p1,p2));m(lnsg(u,p2,p5));m(lnsg(u,p5,p6));m(lnsg(u,p6,p1));
        ar*=sign(3.5-sign(X-3.*h)-sign(-X+5.*h)-sign(Y+C*(X-5.*h))-sign(-Y-C*(X-7.*h)));
        col = mix(col,mix(c2,c1,clamp(abs(X-2.*h)/(2.*h),0.,1.)),s(ar, 1.1));
        ar = 999.;
        m(lnsg(u,p6,p7));m(lnsg(u,p7,p4));m(lnsg(u,p4,p5));m(lnsg(u,p5,p6));
        ar*=sign(3.5-sign(Y+C*(X-7.*h))-sign(-Y-C*(X-9.*h))-sign(Y-C*(X-3.*h))-sign(-Y+C*(X+h)));
        col = mix(col,mix(c3,c4,clamp(abs(X-3.*h)/(3.*h),0.,1.)),s(ar, 1.1));
        ar = 999.;
        m(lnsg(u,p2,p5));m(lnsg(u,p5,p4));m(lnsg(u,p4,p3));m(lnsg(u,p3,p2));
        ar*=sign(3.5-sign(X-5.*h)-sign(-X+6.*h)-sign(Y-C*(X-5.*h))-sign(-Y+C*(X-3.*h)));
        col = mix(col,mix(c6,c5,clamp(abs(X-5.*h)/h,0.,1.)),s(ar, 1.1));
        u.y += 2.*a;
    }
    fragColor = vec4(col,1.0);
}
