//#define GRID_WIN 
#define GRID_LINE 
#define PI 3.1415926535897932384626433832795
#define CELLS 3.0
#define LINE_WIDTH 20.0


float step(float a, float b, float x) {
    return clamp((x - a)/(b - a), 0., 1.);
}

float Grid(float x, float cells, float weight) {
    float s = 1.0 / cells;
    float nx = sin(cells*PI*mod(x, s));
    return step(weight, 0.01 + weight, nx);
}


vec4 gridWin(vec2 uv) {
    // Output to screen
    return texture(iChannel0, fract(uv*3.0));
}

vec4 gridLine(vec2 uv, float lineWidth) {
    vec4 bg = texture(iChannel0, uv);
    float ratio = iResolution.x/iResolution.y;
    float v;

    v = Grid(uv.x, CELLS, lineWidth/ratio) *Grid(uv.y, CELLS, lineWidth);

    vec3 col = vec3(v);

    vec4 color = vec4(1.0, 0.0, 0.0, v);
    return mix(bg, color, 1.0 - v);
}

vec4 gridSplit(vec2 uv) {
    vec4 bg = texture(iChannel0, uv);
    //todo
    return bg + vec4(0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    #ifdef GRID_WIN
        fragColor = gridWin(uv);
    #elif defined(GRID_LINE)
        float lineSize = LINE_WIDTH/iResolution.y;
        fragColor = gridLine(uv, lineSize);
    #else
        fragColor = gridSplit(uv);
    #endif    
}
