#define particles 80.0
#define PI 3.14159


float circ(vec2 uv, float r)
{
    return smoothstep(length(uv)-r, length(uv)-r+0.001+(abs(sin(iTime*0.5))*0.001) , 0.0001);
}

float ring(vec2 uv, float r, float t, float w)
{
    return circ(uv,r)-circ(uv,t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5 * iResolution.xy)/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.xy;

    float t = iTime * (0.01 + (mouse.x*0.001));
    float ct = iTime * 0.8;
    vec3 col = vec3(ring(uv,0.5,0.49,0.0001));
    const float incr = PI/particles;
    for (float i = particles; i > 0.0; i -= incr)
    {
        vec2 bv = uv+(vec2(cos(t+i),sin(t+i)) * cos(t*i)*0.49);
        bv *= uv;
        col += circ(bv,0.001 + mouse.y*0.001);
    }
    vec3 tint = vec3(0.12 * abs(sin(ct * 0.2)), 0.09 * abs(sin(ct * 0.16)), 0.18 * abs(sin(ct * 0.18)));
    fragColor = vec4(pow(tint*col,vec3(0.4545)),1.0);
}
