#define pi 3.14159

#define thc(a,b) tanh(a*cos(b))/tanh(a)

mat2 Rot(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b*cos( 6.28318*(c*t+d) );
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

float dlength(vec2 uv) {
    return abs(uv.x) + abs(uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // change me
    float a1 = 0.;
    float a2 = 0.;
    int n = 3;
    float l = -24.; // 0. looks less disgusting

    float m = 0.3 + 0.15 * thc(5., 0.6 * iTime); 

    uv *= Rot(0. * iTime + a1 * atan(uv.x, uv.y));
    for (int i = 0; i < n; i++) {
        //uv *= Rot(a1 * atan(uv.x, uv.y));
        uv = abs(uv) - m;
        m *= 0.5;
    }

    uv *= Rot(l * length(uv) + a2 * atan(uv.x, uv.y) + iTime);
    vec2 s = 2. * step(uv, vec2(0)) - 1.;
    vec2 id = 0.5 + 0.25 * s;

    vec3 e = vec3(1);
    // most of this is unnecessary
    vec3 col = pal(id.x * id.y + 0.1 + 0.0675 * (s.x + 5.) * (s.y -5.), 1.2 * e, e, e,
                   0.5 * vec3(0,1,2)/3.);
    //col = sqrt(col);

    fragColor = vec4(col,1.0);
}
