void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 bUv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col;

    float p;

    vec2 itv = iMouse.xy / iResolution.xy;

    itv.x = itv.x==0. ? .52 : itv.x;
    itv.y = itv.y==0. ? .45 : itv.y;

    itv -= .5;

    float d = atan(itv.x, itv.y) * 6.;

    uv -= itv;


    for( float i = 0.; i < 4.; i++ ){
        uv.x += cos( ( uv.x, i ) * uv.y ) + sin( atan( uv.x - .5, uv.y - .5 ) ) - d * .5;
        uv.y -= sin( i * mod( uv.x, .4 ) * .2 ) + mod( uv.x, .05 ) - d * .5;
    }

    vec3 cal = cross( 
        vec3( cos(uv.x), uv.x, cos( uv.x ) ),
        vec3( uv.x, uv.y,
        cos( uv.y ) ) 
    );

    p = cal.x;

    p += smoothstep( uv.x, uv.y, p );

    vec3 col2 = texture( iChannel0, bUv + p * .001 ).rgb;
    col = mix( vec3( 0. ), col2, p * .05 );

    // Output to screen
    fragColor = vec4(col,1.0);
}
