// Display 🌵
Main 
{
    float w = B(U).w;
    Q = (1.+sin(2.+A(U).w+vec4(1,2,3,4)))*
    (pow(w,10.));
}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)
#define box for(int x=-1;x<=1;x++)for(int y=-1;y<=1;y++)
#define box2 for(int x=-2;x<=2;x++)for(int y=-1;y<=1;y++)

#define r2 0.70710678118
float hash(vec2 p) // Dave H
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

// Forces 🦜
Main
{
    Q = A(U);
    vec4 dQ = vec4(0);
    box if(abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 a = A(U+u);
        float f = 0.1*a.w*(a.w*(a.w-.9)+.1);
        dQ.xy -= f*u;
    }
    Q += dQ;
    Q.y -= .1/R.y;
    vec2 M = 1.5*R;
    if (iMouse.z>0.) M = iMouse.xy;
    if(length(U-M)<.02*R.y)Q = vec4(.1*normalize(iMouse.xy-0.5*R),0,1.);
    if (length(Q.xy)>.5) Q.xy = .5*normalize(Q.xy);
    if (iFrame < 1) Q = vec4(0,0,0,step(U.y,.5*R.y)*(1.-step(abs(U.x-.5*R.x),0.1*R.x)));
    if (U.x < 1.||R.x-U.x<1.) Q.xy *= 0.;
    if (U.y < 1.||R.y-U.y<1.) Q.xy *= 0.;
    if (R.y-U.y<1.)Q.w*=0.;

}

// Advect 🐿
Main
{
    Q = A(U);
    vec4 dQ = vec4(0);
    box if(abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 q = A(U+u);
        vec2 a = Q.xy,
             b = q.xy+u;
       float ab = dot(u,b-a);
       float i = dot(u,(0.5*u-a))/ab;
       float j = .5+.5*max(1.-1.*Q.w*q.w,0.);
       float k = .5+.5*max(1.-1.*Q.w*q.w,0.);
       float wa = 0.25*Q.w*min(i,j)/j;
       float wb = 0.25*q.w*max(k+i-1.,0.)/k;
        dQ.xyz += Q.xyz*wa+q.xyz*wb;
        dQ.w += wa+wb;

    }
    if (dQ.w>0.)dQ.xyz/=dQ.w;
    Q = dQ;

}

Main {
    Q = B(U);
    box2 {
        vec2 u = vec2(x,y);
        vec4 a = B(U+u);
        if (length(a.xy-U)<length(Q.xy-U)){
            Q = a;
        }
    }
    vec4 n = A(Q.xy+vec2(0,1)),
         e = A(Q.xy+vec2(1,0)),
         s = A(Q.xy-vec2(0,1)),
         w = A(Q.xy-vec2(1,0));
    vec2 g = vec2(e.w-w.w,n.w-s.w);

    Q.zw += .04*g;
    if (length(Q.zw)>0.) Q.zw = .5*normalize(Q.zw);
    Q.xy += Q.zw;
    if (iFrame < 1||R.y-U.y<2.) {
        Q = vec4(round(U/3.)*3.+3.*
        2.*hash(vec2(U.x,iFrame))-1.,0,-1);
    }
}

Main {

    Q = B(U);
    vec4 a = A(U);
    Q *= .99;
    Q += .013*exp(-.2*length(a.xy-U));

}
