vec3 SunColour = vec3(1.0,0.90,0.8);
vec3 SunCentre = vec3(1.0,1.0,1.0);

vec3 SkyColour= vec3(0.80,0.69,0.75);

vec3 RoadColour = vec3(0.0,0.1,0.2);
vec3 RoadLineColour = vec3(0.4,0.7,0.9);

float SunSize = 50.0f;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //Y is 0 to 1. X is -1 to Positive 1.
    vec2 uv = vec2((2.0 * fragCoord.x) / iResolution.x,fragCoord.y / iResolution.y);

    vec2 Point = vec2(0.30,0.930);
    Point.x += sin(iTime * 0.2) * 0.1;
    Point.y += cos(iTime * 0.2) * 0.1;

    Point *= iResolution.xy;

    vec3 Colour = vec3(0.0);
    float Strength = 1.0f;
    Colour = mix(SunCentre,SunColour,distance(Point,fragCoord) *0.01);

    //vec2 uv = fragCoord.xy / iResolution.xy;

    Colour = mix(vec3(0.0,0.71,0.43),Colour,distance(Point,uv) * .0050);
    Colour = mix(SunColour * 0.1,SkyColour,Colour + uv.y * 0.5);
    Colour = mix(Colour,Colour * vec3(0.1),smoothstep(0.6,0.6,1.0 - uv.y));

    //road


    vec3 RoadColour = mix(RoadColour,RoadLineColour,pow(sin(fract(uv.x * 10.0 * (1.0 + uv.y))*3.14),10.0));
    Colour = mix(RoadColour,Colour,smoothstep(0.35,.50,uv.y));


    fragColor = vec4(Colour,1.0);

}
