{
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = 1.0- step(0.00001, texture(iChannel0, uv));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 tc = fragCoord.xy / iResolution.xy;

    vec4 tex = texture(iChannel1, tc);
    //vec4 past = texture(iChannel0, tc + vec2(0.01));

    if(iFrame < 1 || iFrame % 1500 == 0 || iMouse.z > 0.0){
    // Seed the feedback
         if(tc.x >0.475 && tc.x < 0.525 && tc.y > 0.475 && tc.y <0.525){
            fragColor = vec4(1.0);
        } else {
            fragColor = vec4(0.0,0.0,0.0,1.0);
        }
    } else {
        fragColor = tex;
    }



}

float rand(float n){return fract(sin(n) * 43758.5453123);}

float noise(float p){
    float fl = floor(p);
    float fc = fract(p);
    return mix(rand(fl), rand(fl + 1.0), fc);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 texel = 1.0 / iResolution.xy;

    vec4 t = texture(iChannel0, uv);

    vec4 n = texture(iChannel0, uv + vec2(0.0, texel.y));
    vec4 e = texture(iChannel0, uv + vec2(texel.x, 0.0));
    vec4 w = texture(iChannel0, uv + vec2(-texel.x, 0.0));
    vec4 s = texture(iChannel0, uv + vec2(0.0, -texel.y));

    if(iFrame % 2 == 0){ 
        fragColor = abs(e - t) + abs(w - t) ;
    } else {
        fragColor = abs(n - t) + abs(s - t) ;
    }



}
