// golfed version of https://shadertoy.com/view/7sXXRf

#define T(U,l)     texture( iChannel0, U, l ) 
#define D(k)       clamp( 1. - abs( fract(k*f*l-.5) - .5 ) / l/d/k, 0.,1.)

void mainImage( out vec4 O, vec2 u )
{
    vec2 U = u / iResolution.xy;

	float f = 12.*T( U , 2. ).r + .001*T(U/.1, 2. ).r, 
          d =  fwidth(f),
          w = -log2( 44. * d ),
          l =  exp2(ceil(w)); 

     O = sqrt( mix( D(6.), D(12.), fract(w) ) * T( U,0.) );
}
