precision highp float;

const float e = 2.71;
const float decayFactor = 50.;
const float decayThreshold = pow(e, decayFactor);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2. * fragCoord/iResolution.xy - 1.;
    uv.x = uv.x * iResolution.x/iResolution.y;
    uv = mod(uv * 5., 1.);
    float width = pow(e, -abs(uv.x) * decayFactor);
    float height = pow(e,-abs(uv.y) * decayFactor);
    float mask = (width+ 1./2.) * (height+ 1./2.);
    vec3 color = mix(vec3(0., 0., 0.), vec3(0., 0., 1.), mask);

    fragColor = vec4(color, 1.);
}
