#define PI 3.14159265359

vec3 hsl2rgb(in vec3 c)
{
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0,0.0,1.0);
    return c.z+c.y*(rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 m = iMouse.xyzw/iResolution.xyxy;
    vec3 col = hsl2rgb(vec3(uv.x-iTime/PI, 1.0, 0.5-abs(uv.y-0.5)));
    vec2 u = uv-vec2(0.5, 0.5);
    float r = 0.01; // line width
    float sm = 0.03; // smoothness
    float amp = 2.0;
    float freq = 1.0; freq *= PI*2.0;
    vec3 blend = vec3(0.0);
    for(float i = 1.0; i <= 50.0; i++)
    {
        blend += vec3(smoothstep(0.0, sm, r-abs(u.y+amp*cos(iTime+i/PI)/2.0/sqrt(i)*sin(freq*u.x+iTime/sqrt(i/PI)+m.x*10.0))));
    }
    fragColor = vec4(col*blend*3.0, 1.0);
}
