 float amplitude = 0.03f; // 0.01 to 0.1, 0.03
 float speed = 1.0f; // 0.1 to 10, 1
 float frequency = 6.0f; // 1 to 30, 6
 float opacity = 0.6f; // 0 to 1, 0.5


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pulse = sin(iTime * speed - frequency * uv);
    vec2 coord = uv + amplitude * vec2(pulse.x, -pulse.y);
    fragColor = texture(iChannel0,coord) * opacity;

}
