float N21 (vec2 p){
    p=fract(p*vec2(233.34, 851.73));
    p+=dot(p, p+23.45);
    return fract(p.x*p.y);
}

vec2 N22(vec2 p) {
    float n=N21(p);
    return vec2(n, N21(p+n));
}

vec2 GetPos(vec2 id, vec2 offs){
    vec2 n=N22(id+offs);
    return offs+sin((n+1.0)*iTime/1.5)*0.6;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/max(iResolution.x, iResolution.y);

    float m= 1.;

    vec2 uv5=uv*5.0;
    vec2 guv=fract(uv5)-0.48;
    vec2 id=floor(uv5);

    vec2 p[9];

    int i=0;
    for (float y=-1.0; y<=1.0; y++) {
        for (float x=-1.0; x<=1.0; x++) {
            p[i]=GetPos(id, vec2(x,y));
            i++;
        }
    }

    for (int i=0; i<9; i++) {
        m*=distance(guv, p[i]);
    }

    vec3 col = texture( iChannel0, vec2(m/30., 0.0)).xyz;

    if (guv.x>.49999 || guv.y>.49999) {
        col = vec3(1.0);
    }


    fragColor = vec4(col, 1.0);
}
