float _pi = 3.14159;

vec3 hsv_to_rgb (float h, float s, float v)
{
    if (s == 0.0) {
        return vec3(v);
    }
    float i = floor(h * 6.0);
    float f = h * 6.0 - i;
    float p = v * (1.0 - s);
    float q = v * (1.0 - f * s);
    float t = v * (1.0 - (1.0 - f) * s);
    if (i >= 6.0 || i < 1.0) return vec3(v,t,p);
    else if (i >= 1.0 && i < 2.0) return vec3(q,v,p);
    else if (i >= 2.0 && i < 3.0) return vec3(p,v,t);
    else if (i >= 3.0 && i < 4.0) return vec3(p,q,v);
    else if (i >= 4.0 && i < 5.0) return vec3(t,p,v);
    else if (i >= 5.0 && i < 6.0) return vec3(v,p,q);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * fragCoord/iResolution.xy - 1.0;
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;
    const int n = 5;
    float a = -0.2 * iTime * _pi;
    float len = 0.6, radius = 0.75 / float(n);
    float h = 0.0, angle;
    vec2 pos;
    for(int i = 0; i < n; i ++) {
        angle = a + float(i) * (_pi + _pi) / float(n);
        pos = vec2(1.5 * len * cos(angle), len * sin(angle + angle));
        h += radius/length(uv - pos);
    }
    h = fract(h + 0.1 * iTime);
    vec3 col = hsv_to_rgb(h,1.0,1.0);
    fragColor = vec4(col,1.0);
}
