vec2 iSqrt(vec2 n){

    float real = n.x * n.x + -1.0 * n.y * n.y;
    float im = 2.0*n.x*n.y;

    return vec2(real,im);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #define PI 3.14
    precision highp float;  
    // koordinaatisto keskelle
    float zoomi=pow(0.9 + abs(sin(iTime/4.)),14.); //zoomer, not in use
    zoomi= 0.45;
    vec2 pan=vec2(-60., 0.);
    vec2 uv = (fragCoord.xy +pan - (0.5) * iResolution.xy) / iResolution.y / zoomi; // jaetaan 'y' 'llÃ¤ jotta pysyy neliÃ¶n alalla


    float it = 0.0;
    float x = (iMouse.x/iResolution.x-0.5)*2.;
    float y = (iMouse.y/iResolution.y-0.5)*2.;
    vec2 numero = vec2(x,y);

    float max_iter=30.0;
    while ((numero.x*numero.x+numero.y*numero.y) < 4.0 && it <= max_iter) {
    // == sqrt(x^2+y^2) <2.0  ilman juurta thx -> wuffe
        numero=iSqrt(numero);
        numero+=uv;
        it++;
    }
    float red = 0.7 * it / max_iter;
    float green = 0.;

    float blue= 0.4 - (1. * it / max_iter);

    if(it < max_iter-1.){
        green=1.3* it / max_iter;
        blue=1.3* it / max_iter;
    }


    //blue = 1.;
    //aallot
    //green -= 0.03*(8.61-(numero.x*numero.x+numero.y*numero.y));

    // Output to screen
    fragColor = vec4(red, green, blue , 1.0);
}
