float circle(vec2 samplePosition, vec2 offset, float radius)
{
    float d =  length(samplePosition - offset) - radius;
    return d;
}

float smin(float a, float b, float k)
{
    float h = max(k -abs(a-b), 0.0) / k;
    return min(a, b) - h*h*k*0.25;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    float radius = 0.1;
    float c1  = circle(uv, vec2(0.2*sin(iTime), 0.2*cos(iTime)), radius);     
    float c2 = circle(uv, vec2(-0.2 + 0.1*cos(iTime*1.5), 0.2 + 0.1*sin(iTime)), radius);     
    float c3 = circle(uv, vec2(0.25*sin(iTime*1.55 + 2.), 0.25*cos(iTime + 5.)), radius);     
    float c4 = circle(uv, vec2(0.4*sin(iTime*1.2 + 10.), 0.2*cos(iTime*0.5)), radius);     

    float k = .25;
    float dist = smin(c1, c2, k);
    dist = smin(dist, c3, k);
    dist = smin(dist, c4, k);

    float edgeSize = 0.0015;
    vec3 col = vec3(1.) - (vec3(smoothstep(0., edgeSize, dist)) + vec3(smoothstep(0., edgeSize, -dist)));

    // Output to screen
    fragColor = vec4(col, 1.0);
}
